// write a program to print hello world 5 times
package LAB_01;

public class P_1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i; // counter variable
		for (i=0; i<5;i++) {
			System.out.println("Hello");
		}
		System.out.println();
		for (i=5; i>0;i--) {
			System.out.println("World");
		}
	}
}
